    private void btnRegistrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegistrarActionPerformed

        if(this.rdoSugerencia.isSelected()){
        JOptionPane.showMessageDialog(rootPane, "Gracias por su Sugerencia la tendremos en cuenta! Gracias por su tiempo "
                + this.txtNom.getText()+"\n Nosotros le escribirimos a :"+this.ftxtEmail.getText());
        }
        if(this.rdoQueja.isSelected()){
        JOptionPane.showMessageDialog(rootPane, "Gracias por su Queja la tendremos en cuenta! Gracias por su tiempo "
                + this.txtNom.getText()+"\n Nosotros le escribirimos a :"+this.ftxtEmail.getText());
        }if(this.rdoFelicitacion.isSelected()){
        JOptionPane.showMessageDialog(rootPane, "Gracias por su Felicitacion la tendremos en cuenta! Gracias por su tiempo "
                + this.txtNom.getText()+"\n Nosotros le escribirimos a : "+this.ftxtEmail.getText());
        
        }       
        
    }//GEN-LAST:event_btnRegistrarActionPerformed

    private void btnCancelarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCancelarActionPerformed
        
        if(JOptionPane.showConfirmDialog(this,"Desea abandonar la captura?","Salir de Ventana",JOptionPane.YES_NO_OPTION)==0){
        this.dispose();
        }
    }//GEN-LAST:event_btnCancelarActionPerformed

